@@preprocessing
<%_
// 1. 获取当前地点
if (typeof currentLocation === 'undefined') var currentLocation = getvar('stat_data.世界.当前地点', { defaults: '未知' });

// 2. 基础骰子逻辑 (2D6)
var d1 = Math.floor(Math.random() * 6) + 1;
var d2 = Math.floor(Math.random() * 6) + 1;
var result = d1 + d2;
var finalOutput = '';

// 3. 地点特殊数值修正
if (currentLocation.includes('猪坑')) {
    result = 4;
} else if (currentLocation.includes('断头巷')) {
    result = Math.floor(Math.random() * 6) + 1; // 改为1D6
} else if (currentLocation.includes('间宫旅舍') || currentLocation.includes('萨菲尔纪念公园')) {
    result = result + 2; // 2D6+2
}

// 4. 结果数值修正 (巨鲨金融总部)
if (currentLocation.includes('巨鲨金融总部')) {
    if (result === 3 || result === 7 || result === 10) {
        result = 2;
    }
}

// 5. 生成最终关键词
// 优先处理间宫旅舍的特殊输出
if (currentLocation.includes('间宫旅舍') && (result === 11 || result === 12)) {
    finalOutput = '街娼_间宫旅舍1';
} else if (currentLocation.includes('间宫旅舍') && (result >= 13)) {
    // 2D6+2最大是14，这里包含13和14
    finalOutput = '街娼_间宫旅舍2';
} else if (currentLocation.includes('咒术巷') || currentLocation.includes('哥布林市场')) {
    finalOutput = '街娼妖精_' + result;
} else {
    // 默认输出
    finalOutput = '街娼_' + result;
}
_%>
<%= finalOutput %>
<%_ if (currentLocation.includes('娱乐园') || currentLocation.includes('失乐园大厅')) { _%>
街娼_3
街娼_6
<%_ } _%>
<%_ if (currentLocation.includes('神魔智学校')) { _%>
街娼_6
街娼_7
<%_ } _%>
<%_ if (currentLocation.includes('Nobullshit')) { _%>
街娼_11
街娼_12
<%_ } _%>
