<%_
/* --- 变量声明 --- */
// 获取当前地点
if (typeof currentLocation === 'undefined') var currentLocation = getvar('stat_data.世界.当前地点', { defaults: '' });
// 获取当前事件状态（空字符串表示无事件）
if (typeof currentEvent === 'undefined') var currentEvent = getvar('stat_data.事件系统.当前事件', { defaults: '' });
// 获取委托列表（用于判断是否在委托中）
if (typeof currentQuests === 'undefined') var currentQuests = getvar('stat_data.主角.委托', { defaults: {} });

/* --- 逻辑判断 --- */

// 1. 判断是否空闲（无事件且无委托）
// Object.keys(obj).length === 0 用于判断对象是否为空
var isIdle = (currentEvent === '') && (Object.keys(currentQuests).length === 0);

// 2. 判断区域并提取区域名
var targetArea = '';
if (currentLocation.includes('暗巷')) {
    targetArea = '暗巷';
} else if (currentLocation.includes('神魔智街')) {
    targetArea = '神魔智街';
} else if (currentLocation.includes('断头巷')) {
    targetArea = '断头巷';
}

/* --- 随机触发核心 --- */
// 仅当 空闲 且 处于目标区域 时执行
if (isIdle && targetArea !== '') {
    // 投掷判定骰子 (1D100)
    // 既然哥哥允许使用 lodash，这里使用 _.random
    var checkDice = _.random(1, 100);

    // 3. 概率判定：骰值大于 50 才触发
    if (checkDice > 30) {
        // 投掷两个事件骰子 (1D6)
        var d6_1 = _.random(1, 6);
        var d6_2 = _.random(1, 6);

        // 4. 组合并输出关键词
        // 格式示例：随机事件_暗巷_3_5
_%>
事件ID:随机事件_<%- targetArea %>_<%- d6_1 %>_<%- d6_2 %>
<%_
    }
}
_%>
